// Read query and drive file list from current item
const query = $('Webhook').first().json.body.text || "";
const channelId = $('Webhook').first().json.body.channel_id || "";
const files = $input.all().map(item => item.json).filter(f => f && f.name) || [];
const teamid = $('Webhook').first().json.body.team_id;

// ---------- Helper fns ----------
const escapeRegExp = s => s.replace(/[.*+?^${}()|[\]\\]/g, '\\$&');
const normalize = s => (s || '').toString().toLowerCase().replace(/[^a-z0-9]/g, '');

// Utility: extract just the filename from a Slack URL
// Utility: extract just the filename from a Slack URL and strip file id prefix
const extractFileName = (name) => {
  if (!name) return "";
  // If it's a Slack download URL, keep only the filename part
  const idx = name.lastIndexOf("/download/");
  let fname = idx !== -1 ? name.substring(idx + 10) : name;

  // Strip Slack-style file id prefix like "F09HZFR9BA8-Aditya.pdf" ‚Üí "Aditya.pdf"
  const dashIndex = fname.indexOf("-");
  if (dashIndex !== -1) {
    return fname.substring(dashIndex + 1);
  }
  return fname;
};



// Levenshtein distance
function levenshtein(a, b) {
  const m = a.length, n = b.length;
  if (m === 0) return n;
  if (n === 0) return m;
  const dp = Array.from({ length: m + 1 }, () => new Array(n + 1).fill(0));
  for (let i = 0; i <= m; i++) dp[i][0] = i;
  for (let j = 0; j <= n; j++) dp[0][j] = j;
  for (let i = 1; i <= m; i++) {
    for (let j = 1; j <= n; j++) {
      const cost = a[i - 1] === b[j - 1] ? 0 : 1;
      dp[i][j] = Math.min(
        dp[i - 1][j] + 1,
        dp[i][j - 1] + 1,
        dp[i - 1][j - 1] + cost
      );
    }
  }
  return dp[m][n];
}

// ---------- Matching logic ----------
let matchedFiles = [];
let matchMethod = 'none';

if (!files.length) {
  matchedFiles = [];
  matchMethod = 'no-files';
} else if (!query.trim()) {
  matchedFiles = files.map(f => ({ ...f, name: extractFileName(f.name) }));
  matchMethod = 'no-query';
} else {
  const q = query.trim();

  // 1) exact regex
  try {
    const re = new RegExp(escapeRegExp(q), 'i');
    matchedFiles = files.filter(f => re.test(f.name))
      .map(f => ({ ...f, name: extractFileName(f.name) }));
    if (matchedFiles.length) matchMethod = 'regex';
  } catch (e) {}

  // 2) normalized substring
  if (matchedFiles.length === 0) {
    const nQ = normalize(q);
    matchedFiles = files.filter(f => normalize(f.name).includes(nQ))
      .map(f => ({ ...f, name: extractFileName(f.name) }));
    if (matchedFiles.length) matchMethod = 'normalized-substring';
  }

  // 3) fuzzy match
  if (matchedFiles.length === 0) {
    const nQ = normalize(q);
    const sims = files.map(f => {
      const nf = normalize(f.name);
      const dist = levenshtein(nf, nQ);
      const maxLen = Math.max(nf.length, nQ.length, 1);
      const sim = 1 - dist / maxLen;
      return { file: f, sim };
    }).sort((a, b) => b.sim - a.sim);

    const THRESHOLD = 0.45;
    const fuzzyMatches = sims.filter(s => s.sim >= THRESHOLD).map(s => ({
      id: s.file.id,
      name: extractFileName(s.file.name),
      score: Number(s.sim.toFixed(3))
    }));

    if (fuzzyMatches.length) {
      matchedFiles = fuzzyMatches;
      matchMethod = 'fuzzy';
    } else if (sims.length) {
      matchedFiles = sims.slice(0, 3).map(s => ({
        id: s.file.id,
        name: extractFileName(s.file.name),
        score: Number(s.sim.toFixed(3))
      }));
      matchMethod = 'top-suggestions';
    }
  }
}

// Output with dynamic channelId
return [{
  json: {
    query,
    matchMethod,
    matchedFiles: matchedFiles.map(f => ({
      ...f,
      name: extractFileName(f.name)
    })),
    channelId,
    teamid,
    message: matchedFiles.length === 0 ? "No files found" : undefined
  }
}];

// Get the raw payload string from the correct location
const rawPayload = $json.body?.payload || $json.payload;

if (!rawPayload) {
  throw new Error("No Slack interactive payload found in request");
}

// Parse the Slack payload JSON string
const parsed = JSON.parse(rawPayload);

// Extract the file ID from the button's value
const fileId = parsed?.actions?.[0]?.value || null;

// Extract channel and user info
const channelId = parsed?.channel?.id || null;
const userId = parsed?.user?.id || null;

// Try to get the file name from the block before the actions block
let fileName = null;
if (parsed?.message?.blocks && parsed.actions?.[0]) {
  const actionBlockId = parsed.actions[0].block_id;

  // Find the index of the actions block
  const idx = parsed.message.blocks.findIndex(
    b => b.type === "actions" && b.block_id === actionBlockId
  );

  if (idx > 0 && parsed.message.blocks[idx - 1].type === "section") {
    // Remove the :page_facing_up: icon and trim spaces
    fileName = parsed.message.blocks[idx - 1].text.text
      .replace(/^:page_facing_up:\s*/, "")
      .trim();
  }
}

return [{
  json: {
    fileId,
    fileName,
    channelId,
    userId,
    raw: parsed
  }
}];

