// PocketBase base URL
const POCKETBASE_URL = 'https://pb.levelingupdata.com';
const COLLECTION = 'users';

// Get email from previous Slack node
const emailToCheck = $('HTTP Request3').first().json.user?.profile?.email;
if (!emailToCheck) {
  return [{ success: false, message: "No email provided from previous node" }];
}

async function main() {
  // Step 1: Search for the user by email
  const searchUrl = `${POCKETBASE_URL}/api/collections/${COLLECTION}/records?filter=${encodeURIComponent(`email="${emailToCheck}"`)}`;

  const searchRes = await this.helpers.request({
    method: 'GET',
    uri: searchUrl,
    json: true
  });

  // Step 2: If found, update slack_status
  if (searchRes?.items?.length > 0) {
    const user = searchRes.items[0];
    const updateUrl = `${POCKETBASE_URL}/api/collections/${COLLECTION}/records/${user.id}`;

    const updateRes = await this.helpers.request({
      method: 'PATCH',
      uri: updateUrl,
      body: { slack_status: true },
      json: true
    });

    return [{
      success: true,
      message: `User ${emailToCheck} found and slack_status updated.`,
      userId: updateRes.id,
      slack_status: updateRes.slack_status
    }];
  } else {
    // Step 3: No user found
    return [{
      success: false,
      message: `No user found with email ${emailToCheck}`
    }];
  }
}

return await main();

